#include "leet.cpp"
#include <gtest/gtest.h>
#include "glog/stl_logging.h"
#include "glog/logging.h"

TEST(kk, t0) {
  Solution sol;
  EXPECT_EQ(sol.longestSubstring("abcdedghijklmnopqrstuvwxyz", 2), 0);
}
TEST(kk, t1) {
  Solution sol;

  EXPECT_EQ(sol.longestSubstring("a", 1), 1);
  EXPECT_EQ(sol.longestSubstring("aaabb", 3), 3);
  EXPECT_EQ(sol.longestSubstring("ababbc", 2), 5);
  EXPECT_EQ(sol.longestSubstring("zzzzzzzzzzaaaaaaaaabbbbbbbbhbhbhbhbhbhbhicbcbcibcbccccccccccbbbbbbbbaaaaaaaaafffaahhhhhiaahiiiiiiiiifeeeeeeeeee", 10), 21);

  string s = "";
  EXPECT_EQ(sol.longestSubstring(s, 301), 301);
}

