import unittest

class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        def get_pd(s, start, ell, pd):
            end = start + ell - 1
            pos = (start, end)
            if pos in pd:
                return pd[pos]

            if ell == 1:
                ret = True
            elif ell == 2:
                ret = bool(s[start] == s[end])
            elif ell > 2 and s[start] == s[end]:
                ret = get_pd(s, start + 1, ell - 2, pd)
            else:
                ret = False
            pd[pos] = ret
            return ret

        if not s:
            return ""
        pd = {}
        lens = len(s)
        for ell in range(lens, 0, -1):
            for start in range(0, lens - ell + 1):
                if get_pd(s, start, ell, pd):
                    return s[start:start+ell]
        return s[0]


class SolTest(unittest.TestCase):
    def test_1(self):
        sol = Solution()
        self.assertEqual(len(sol.longestPalindrome("babad")), 3)
        self.assertEqual(len(sol.longestPalindrome("cbbd")), 2)
        self.assertEqual(len(sol.longestPalindrome("a")), 1)
        s = ""
        self.assertEqual(len(sol.longestPalindrome(s)), len(s))
        s = ""
        self.assertEqual(len(sol.longestPalindrome(s)), len(s))
        s = ""
        self.assertEqual(len(sol.longestPalindrome(s)), 500)


if __name__ == "__main__":
    unittest.main()
