import unittest

class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        if not s:
            return ""
        if self.is_palindrome(s):
            return s
        ret = ""
        lens = len(s)
        palindrome = {}
        for offset in range(0, lens):
            for start in range(0, lens):
                end = start + offset
                check = False
                if end >= lens:
                    continue

                if start == end:
                    check = True
                elif offset == 1 and s[start] == s[end]:
                    check = True
                elif offset > 1 and s[start] == s[end] and palindrome[(start+1, end-1)]:
                    check = True

                palindrome[(start, end)] = check
                if check and offset + 1 > len(ret):
                    ret = s[start:end+1]

        return ret

    def is_palindrome(self, s):
        lens = len(s)
        lens_1 = lens - 1
        i = 0
        j = lens_1
        while i <= j:
            if s[i] != s[j]:
                return False
            i += 1
            j -= 1
        return True

class SolTest(unittest.TestCase):
    def test_1(self):
        sol = Solution()
        self.assertEqual(len(sol.longestPalindrome("babad")), 3)
        self.assertEqual(len(sol.longestPalindrome("cbbd")), 2)
        self.assertEqual(len(sol.longestPalindrome("a")), 1)
        s = ""
        self.assertEqual(len(sol.longestPalindrome(s)), len(s))
        s = ""
        self.assertEqual(len(sol.longestPalindrome(s)), len(s))
        s = ""
        self.assertEqual(len(sol.longestPalindrome(s)), 500)


if __name__ == "__main__":
    unittest.main()
